    pm.expect(json.data.length).to.eql(0);  // this will fail
});

// Fail Case 3: Checking incorrect support message
pm.test("Fail Case: Wrong support text (Expected fail)", function () {
    pm.expect(json.support.text).to.eql("Sample Wrong Text");
});

requests.preRequestScript:
pm.environment.set("host", "reqres.in");
pm.environment.set("basepath", "api");

// Log URL being called
console.log("Calling:", `https://${pm.environment.get("host")}/${pm.environment.get("basepath")}/users`);

requests.testScript:
let json = pm.response.json();

// -----------------------
// ✔ SUCCESS TEST CASES
// -----------------------

// Test 1: Status code check
pm.test("Status code is 200", function () {
    pm.response.to.have.status(200);
});

// Test 2: Page information validation
pm.test("Page information is correct", function () {
    pm.expect(json).to.have.property("page");
    pm.expect(json).to.have.property("per_page");
    pm.expect(json).to.have.property("total");
    pm.expect(json).to.have.property("total_pages");
});

// Test 3: Data array exists and is an array
pm.test("Data array exists", function () {
    pm.expect(json.data).to.be.an("array");
});

// Test 4: Ensure 6 users are returned
pm.test("6 users are returned", function () {
    pm.expect(json.data.length).to.eql(6);
});

// Test 5: Validate fields of first user
pm.test("First user contains expected fields", function () {
    let user = json.data[0];
    pm.expect(user).to.have.property("id");
    pm.expect(user).to.have.property("email");
    pm.expect(user).to.have.property("first_name");
    pm.expect(user).to.have.property("last_name");
    pm.expect(user).to.have.property("avatar");
});

// Console logs
console.log("Total Users:", json.data.length);
console.log("First User Email:", json.data[0]?.email);


// -----------------------
// ❌ INTENTIONAL FAILURE CASES
// -----------------------

// Fail 1: Wrong total count (expected fail)
pm.test("Fail Case: Total users is 10 (Expected fail)", function () {
    pm.expect(json.total).to.eql(10);
});

// Fail 2: Data array is empty (expected fail)
pm.test("Fail Case: Data array is empty (Expected fail)", function () {
    pm.expect(json.data.length).to.eql(0);
});

// Fail 3: Metadata missing (expected fail)
pm.test("Fail Case: _meta block is missing (Expected fail)", function () {
    pm.expect(json).to.not.have.property("_meta");
});

// Fail 4: Incorrect support text (expected fail)
pm.test("Fail Case: Support text is incorrect (Expected fail)", function () {
    pm.expect(json.support.text).to.eql("Wrong support text here");
});

